mapper.update:

update repay_error_record
set `status` = #{status,jdbcType=INTEGER},
update_time = #{updateTime,jdbcType=TIMESTAMP},
note = #{note,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select

,ub.name AS userName,
ubc.bank_card_no AS bankCardNo,
ubc.opening_bank AS openingBank,
pc.name AS channelName
from repay_error_record t
left join user_basic ub on ub.id = t.uid
left join user_bank_card ubc on ubc.id = t.card_id
left join payment_channel pc on pc.id = t.channel_id
where 1=1

  and t.id=#{id,jdbcType=INTEGER}


  and t.uid=#{uid,jdbcType=INTEGER}


  and t.trade_id=#{tradeId,jdbcType=INTEGER}


  and t.order_id=#{orderId,jdbcType=VARCHAR}


  and t.type=#{type,jdbcType=INTEGER}


  and t.amount=#{amount,jdbcType=DOUBLE}


  and t.card_id=#{cardId,jdbcType=INTEGER}


  and t.service_charge=#{serviceCharge,jdbcType=DOUBLE}


  and t.channel_id=#{channelId,jdbcType=INTEGER}


  and t.payment_time=#{paymentTime,jdbcType=TIMESTAMP}


  and t.other_params=#{otherParams,jdbcType=VARCHAR}


  and t.error_msg=#{errorMsg,jdbcType=VARCHAR}


  and t.status=#{status,jdbcType=INTEGER}


  and t.retry_count=#{retryCount,jdbcType=INTEGER}


  and t.ref_record_id=#{refRecordId,jdbcType=INTEGER}


  and t.create_time=#{createTime,jdbcType=TIMESTAMP}


  and t.update_time=#{updateTime,jdbcType=TIMESTAMP}


  and t.create_time >= #{startTime}


  and t.create_time <= #{endTime}


  and ub.name = #{userName}

order by t.create_time desc
