mapper.select:
        select

        ,fu.status AS bbsStatus
        ,ub1.name  AS refererUName
        from user_basic ub
        LEFT JOIN forum_user fu ON ub.id = fu.uid
        left join user_basic ub1 on ub.referer_uid = ub1.id
        where 1=1

            and ub.id=#{id,jdbcType=INTEGER}


            and ub.phone=#{phone,jdbcType=VARCHAR}


            and ub.password=#{password,jdbcType=VARCHAR}


            and ub.name=#{name,jdbcType=VARCHAR}


            and ub.sex=#{sex,jdbcType=INTEGER}


            and ub.id_card=#{idCard,jdbcType=VARCHAR}


       		 and ub.idc_img_url1 = #{idcImgUrl1,jdbcType=VARCHAR},


	        and ub.idc_img_url2 = #{idcImgUrl2,jdbcType=VARCHAR},


            and ub.referer_uid=#{refererUid,jdbcType=INTEGER}


            and ub.type=#{type,jdbcType=INTEGER}


        and ub.type!=2


            and ub.status=#{status,jdbcType=INTEGER}


            and ub.create_time=#{createTime,jdbcType=TIMESTAMP}


            and ub.update_time=#{updateTime,jdbcType=TIMESTAMP}

        select

        ,ub1.name AS refererUName
        from user_basic ub
        left join user_basic ub1 on ub.referer_uid = ub1.id
        where 1=1

            and ub.id=#{id,jdbcType=INTEGER}


            and ub.phone=#{phone,jdbcType=VARCHAR}


            and ub.password=#{password,jdbcType=VARCHAR}


            and ub.name=#{name,jdbcType=VARCHAR}


            and ub.sex=#{sex,jdbcType=INTEGER}


            and ub.id_card=#{idCard,jdbcType=VARCHAR}


	        and ub.idc_img_url1 = #{idcImgUrl1,jdbcType=VARCHAR},


	        and ub.idc_img_url2 = #{idcImgUrl2,jdbcType=VARCHAR},


            and ub.referer_uid=#{refererUid,jdbcType=INTEGER}

        and ub.type=2

            and ub.status=#{status,jdbcType=INTEGER}


            and ub.create_time=#{createTime,jdbcType=TIMESTAMP}


            and ub.update_time=#{updateTime,jdbcType=TIMESTAMP}

SELECT DATE_FORMAT(temp.totalDay,'%m/%d') AS statisticsKey, count(ub.id) AS statisticsValue FROM (
SELECT DATE_SUB(CURDATE(), INTERVAL #{listItem} DAY) AS totalDay
) AS temp LEFT JOIN user_basic ub on date(ub.create_time) = temp.totalDay AND ub.status = 0 GROUP BY temp.totalDay

SELECT temp.totalDay AS statisticsKey, count(ub.id) AS statisticsValue FROM (
SELECT DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL #{listItem} MONTH),'%Y/%m') AS totalDay
) AS temp LEFT JOIN user_basic ub on date_format(ub.create_time,'%Y/%m') = temp.totalDay AND ub.status = 0 GROUP BY temp.totalDay
